// JavaScript Document
$('#banner').owlCarousel({
   	 loop:true,	// cho lap lại
	items:1,	// xuất hiện 1 ảnh thôi
	smartSpeed:1000,   	// tốc độ thay đổi ảnh
	nav:true,
	autoplay:true,		// cho phép tự động chạy
    autoplayTimeout:10000,  // thời gian chờ khi chuyển ảnh khi chạy tự động

	  })
